import { defineStore } from 'pinia';
import { ref } from 'vue';

export const useAuthStore = defineStore('auth', () =&gt; {

  const user = ref({
    username: localStorage.getItem('username') || null,
    email: null,
  });

  function login(userData) {
    user.value = userData;
    localStorage.setItem('username', userData.username);
  }

  if (localStorage.getItem('username')) {
    user.value.username = localStorage.getItem('username');
  }

  function logout() {
    user.value = {
      username: null,
      email: null,
      userId: null
    };

    localStorage.removeItem('username');
    localStorage.removeItem('userId');
    localStorage.removeItem('notes');
    localStorage.removeItem('tags');
  }

  function getCurrentUser() {
    return user.value;
  }

  function getUsername() {
    return user.value.username || localStorage.getItem('username');
  }

  function getEmail() {
    return user.value.email;
  }

  function setUserId(id) {
    user.value.userId = id;
    localStorage.setItem('userId', id);
  }

  function getUserId() {
    return localStorage.getItem('userId');
  }

  function setNotes(notes) {
    user.value.notes = notes;
    localStorage.setItem('notes', JSON.stringify(notes));
  }

  function getNotes() {
    return JSON.parse(localStorage.getItem('notes'));
  }

  function setTags(tags) {
    user.value.tags = tags;
    localStorage.setItem('tags', JSON.stringify(tags));
  }

  function getTags() {
    return localStorage.getItem('tags').split(",");
  }

  return { user, login, logout, getCurrentUser, getUsername, getEmail, 
           setUserId, getUserId, setNotes, getNotes, setTags, getTags };
});

import { defineStore } from 'pinia';
import { ref, watch } from 'vue';

export const useNotesStore = defineStore('notes', () =&gt; {

  const notes = ref(JSON.parse(localStorage.getItem('notes') || '[]'));

  function addNote(note) {
    notes.value.push({
      ...note,
      starred: true,
    });
    saveNotesToLocalStorage();
  }

  function removeNote(noteId) {
    notes.value = notes.value.filter(note =&gt; note.note_id !== noteId);
    saveNotesToLocalStorage();
  }

  function starNote(noteId) {
    const note = notes.value.find(note =&gt; note.note_id === noteId);

    if (note) {
      note.starred = true;
      console.log(`Note with ID ${noteId} starred.`);
      saveNotesToLocalStorage();
    }
  }

  function unstarNote(noteId) {
    const note = notes.value.find(note =&gt; note.note_id === noteId);
    if (note) {
      note.starred = false;
      console.log(`Note with ID ${noteId} unstarred.`);
      saveNotesToLocalStorage();
    }
  }

  function getStarredNotes() {
    return notes.value.filter(note =&gt; note.starred);
  }

  function getNumberOfStoredNotes() {
    return notes.value.length;
  }

  function isNoteStarred(note) {
    const foundNote = notes.value.find(n =&gt; n.note_id === note.note_id);
    return foundNote ? foundNote.starred : false;
  }

  function saveNotesToLocalStorage() {
    localStorage.setItem('notes', JSON.stringify(notes.value));
  }

  watch(notes, (newNotes) =&gt; {
    saveNotesToLocalStorage();
  }, { deep: true });

  return {
    notes,
    addNote,
    removeNote,
    starNote,
    unstarNote,
    getStarredNotes,
    getNumberOfStoredNotes,
    isNoteStarred
  };
});

export const formatDate = (dateString) =&gt; {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

