package com.springbackend.demo.repositories;

import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.stereotype.Repository;
import com.springbackend.demo.models.User;

@Repository
public interface UserRepository extends JpaRepository&lt;User, Long&gt; {
	User findByFirstname(String firstname);
}

package com.springbackend.demo.services;

import com.springbackend.demo.models.User;
import com.springbackend.demo.repositories.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.Optional;

@Service
public class UserService {

	@Autowired
	private UserRepository userRepository;

	public User saveUser(User user) {
		if (userRepository.findByFirstname(user.getFirstname()) != null) {
			throw new IllegalArgumentException("User with the name " + user.getFirstname() + " already exists");
		}

		return userRepository.save(user);
	}

	public Optional&lt;User&gt; getUserByFirstname(String firstname) {
		return Optional.ofNullable(userRepository.findByFirstname(firstname));
	}
}

package com.springbackend.demo.controllers;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import com.springbackend.demo.models.User;
import com.springbackend.demo.services.UserService;

@RestController
@RequestMapping("/users")
public class UserController {

	@Autowired
	private UserService userService;

	@PostMapping
	public ResponseEntity&lt;?&gt; createUser(@RequestBody User user) {
		try {
			User createdUser = userService.saveUser(user);
			return ResponseEntity.status(201).body(createdUser);
		} catch (IllegalArgumentException e) {
			return ResponseEntity.status(400).body(e.getMessage());
		}
	}

	@GetMapping("/{firstname}")
	public ResponseEntity&lt;User&gt; getUserByFirstname(@PathVariable String firstname) {
		return userService.getUserByFirstname(firstname)
			.map(user -&gt; ResponseEntity.ok().body(user))
			.orElseGet(() -&gt; ResponseEntity.status(404).body(null));
	}
}

