import os
import json
import webbrowser
import googleapiclient.discovery
import google_auth_oauthlib.flow
import google.auth.transport.requests
import google.oauth2.credentials
from googleapiclient.discovery import build
from termcolor import colored as c
from dotenv import load_dotenv

load_dotenv()

CLIENT_SECRETS_FILE = "client_secret.json"

SCOPES = ['https://www.googleapis.com/auth/youtube.force-ssl']

def get_authenticated_service():
	credentials = None

	if os.path.exists('token.json'):
		with open('token.json', 'r') as token:
			credentials = google.oauth2.credentials.Credentials.from_authorized_user_file('token.json', SCOPES)

	if not credentials or not credentials.valid:
		if credentials and credentials.expired and credentials.refresh_token:
			credentials.refresh(google.auth.transport.requests.Request())
		else:
			flow = google_auth_oauthlib.flow.InstalledAppFlow.from_client_secrets_file(CLIENT_SECRETS_FILE, SCOPES)
			credentials = flow.run_local_server(port=0)

		with open('token.json', 'w') as token:
			token.write(credentials.to_json())

	return build('youtube', 'v3', credentials=credentials)


youtube = get_authenticated_service()

def get_playlist_videos(playlist_id):
	videos = []
	next_page_token = None

	while True:
		request = youtube.playlistItems().list(
			part="snippet",
			playlistId=playlist_id,
			maxResults=50,
			pageToken=next_page_token
		)

		response = request.execute()

		for item in response['items']:
			video_id = item['snippet']['resourceId']['videoId']
			video_title = item['snippet']['title']

			video_info = {
				'video_id': video_id,
				'video_title': video_title
			}

			videos.append(video_info)

		next_page_token = response.get('nextPageToken')

		if not next_page_token:
			break

	return videos


def save_playlist_to_account(videos, new_playlist_title):

	create_playlist_request = youtube.playlists().insert(
		part="snippet,status",
		body={
			"snippet": {
				"title": new_playlist_title,
				"description": "Playlist created using YouTube API",
				"tags": ["sample playlist", "API call"],
				"defaultLanguage": "en"
			},
			"status": {
				"privacyStatus": "private"
			}
		}
	)

	create_playlist_response = create_playlist_request.execute()
	playlist_id = create_playlist_response['id']

	count = 0

	for video in videos:
		if count &lt; 5:
			youtube.playlistItems().insert(
				part="snippet",
				body={
					"snippet": {
						"playlistId": playlist_id,
						"resourceId": {
							"kind": "youtube#video",
							"videoId": video['video_id']
						}
					}
				}
			).execute()
		count += 1

	print(c(f"Playlist '{new_playlist_title}' created and videos added.", 'green'))

	return playlist_id


def delete_playlist_from_account(playlist_id):
	youtube.playlists().delete(id=playlist_id).execute()
	print(c(f"Playlist with id: '{playlist_id}' successfully deleted.", 'red'))

playlist_link = "https://www.youtube.com/playlist?list=PLDIoUOhQQPlXr63I_vwF9GD8sAKh77dWU"
