const express = require('express');
const bodyParser = require('body-parser');
const { Sequelize, DataTypes } = require('sequelize');
const path = require('path');
const portfinder = require('portfinder');

const app = express();
const BASE_PORT = 3000;

// Middleware
app.use(bodyParser.json());

// Database setup
const sequelize = new Sequelize({
  dialect: 'sqlite',
  storage: path.join(__dirname, 'items.db'),
});

const Item = sequelize.define('Item', {
  name: {
    type: DataTypes.STRING,
    allowNull: false,
  }
}, {
  timestamps: false
});

// Initialize the database
async function initDb() {
  await sequelize.sync();
  console.log("Database initialized");
}

// Serve the index.html file at the root URL
app.get('/', (req, res) =&gt; {
  res.sendFile(path.join(__dirname, 'public', 'index.html'));
});

// Routes
app.get('/items', async (req, res) =&gt; {
  const items = await Item.findAll();
  res.json(items);
});

app.post('/add-item', async (req, res) =&gt; {
  const { name } = req.body;
  const newItem = await Item.create({ name });
  res.json({ message: "Item added successfully", id: newItem.id });
});

// Start the server
portfinder.basePort = BASE_PORT;

portfinder.getPortPromise()
  .then((PORT) =&gt; {
    app.listen(PORT, async () =&gt; {
      await initDb();
      console.log(`\nServing at port \x1b[94mhttp://localhost:${PORT}/\x1b[0m`);
    });
  })
  .catch((err) =&gt; {
