/**
 * This class implements an array-based list for a maximum of 20 boats
 *
 * @author Michael Totaro
 */
  public class BoatList {

	/** Capacity of the list containing names of Boats */
	public static final int MAX_BOATS = 20;

	/**
	 * Creates an array-based list of boats, adds some boats, removes a boat,
	 * queries if a boat exists in the list, and prints the list.
	 *
	 * @param args command line arguments (not used)
	 */
	public static void main(String[] args) {
		String[] boats = new String[MAX_BOATS];

		int numberOfBoats = 0;

		numberOfBoats = addBoat(boats, numberOfBoats, "Natalie");
		numberOfBoats = addBoat(boats, numberOfBoats, "Wendell");
		numberOfBoats = addBoat(boats, numberOfBoats, "Lori");
		numberOfBoats = addBoat(boats, numberOfBoats, "Steve");
		numberOfBoats = addBoat(boats, numberOfBoats, "Kevin");

		System.out.println("\nBoat List:");
		printList(boats);

		int index = findBoat(boats, "Rosa");

		if (index == -1) {
			numberOfBoats = addBoat(boats, numberOfBoats, "Rosa");
		} else {
			System.out.println(boats[index] + " is located at index " +
							   index + " in the boats list.");
		}

		numberOfBoats = removeBoat(boats, numberOfBoats, "Wendell");

		System.out.println("\nBoat List:");
		printList(boats);
	}

	/**
	 * Adds a boat to the list.
	 *
	 * @param list the array of boat names
	 * @param numberOfBoats the current number of boats in the list
	 * @param boat the boat to add to the list
	 * @return the new number of boats on the list
	 * @throws IllegalArgumentException (with message "No room in list") if list is full
	 */
	public static int addBoat(String[] list, int numberOfBoats, String boat) {

		if (numberOfBoats &gt;= list.length) {
			throw new IllegalArgumentException("No room in list");
		}

		list[numberOfBoats++] = boat;

		return numberOfBoats;
	}

	/**
	 * Prints the list with each element on a separate line.
	 *
	 * @param boats the array of boat names
	 */
	public static void printList(String[] boats) {
		for (String boatName : boats) {
			System.out.println(boatName);
		}
	}

	/**
	 * Removes a boat from the list.
	 *
	 * @param list the array of boat names
	 * @param numOfBoats the current number of boats in the list
	 * @param boat the boat to remove from the list
	 * @return the new number of boat on the list
	 */
	public static int removeBoat(String[] list, int numberOfBoats, String boat) {
		boolean found = false;

		for (int i = 0; i &lt; numberOfBoats; i++) {
			if (found) {
				list[i - 1] = list[i];
			}

			if (list[i].equals(boat)) {
				found = true;
				numberOfBoats--;
			}
		}

		return numberOfBoats;
	}

	/**
	 * Determines if the given boat is in the list.
	 *
	 * @param list the array of boat names
	 * @param boat the boat to find in the list
	 * @return the index of the list element containing boat
	 *         or -1 if boat does not exist in the list
	 */
	public static int findBoat(String[] list, String boat) {
		for (int i = 0; i &lt; list.length; i++) {
			if (list[i] == boat) {
				return i;
			}
		}

		return -1;
	}
}

